function g.tbl.rtcl.shld_show()
	if g.tbl.rtcl.rtcl_slct.value==2 then
		g.tbl.rtcl.ent_bool=g.tbl.rtcl.is_ent()
		return true
	elseif funcs.ME._aim() then
		if g.tbl.rtcl.rtcl_slct.value==1 then
			g.tbl.rtcl.ent_bool=g.tbl.rtcl.is_ent()
