		tbl[_veh]["secondary_color"]=API.veh.get.secondary_color(_veh)
		tbl[_veh]["secondary_colors"]={}
		tbl[_veh]["secondary_colors"]["r"],
		tbl[_veh]["secondary_colors"]["g"],
