	local X,Y,S,R,G,B,A,T,list = g.feat.show_alt_x,g.feat.show_alt_y,g.feat.show_alt_s,g.feat.show_alt_cr,g.feat.show_alt_cg,g.feat.show_alt_cb,g.feat.show_alt_a,g.feat.show_alt_t,g.tbl.dist_type_str
