		funcs.vec(pos.x+(0.01*(g.RES_H+(1-g.RES_H))),pos.y*2),
		funcs.vec(pos.x+(0.01*(g.RES_H+(1-g.RES_H))),pos.y*2),S.value*g.RES_H,
		funcs.get.rgba_to_int(R.value,G.value,B.value,A.value),
		(1 << 1)+(1<<2),nil)
		YIELD(0)
	end
