			funcs.doo.overlay(text,g._W_B_cr.value,g._W_B_cg.value,g._W_B_cb.value,g._W_B_a.value,g._W_B_s.value/300,g._W_B_f.value,g._W_B_x.value/300,g._W_B_y.value/300)
