	g.tbl.tp_time_hist.history[1] = my_pos
	local function in_my_grid(old_pos,new_pos,_max)
		if mth.abs(new_pos.x - old_pos.x) > _max or mth.abs(new_pos.y - old_pos.y) > _max or mth.abs(new_pos.z - old_pos.z) > _max then
