			for i=1,#g.tbl.PID_INFO.undead_blip do
				if ntv.is.blip_exist(g.tbl.PID_INFO.undead_blip[i]) and funcs.get.dist_pospos_v2(ui.get_blip_coord(g.tbl.PID_INFO.undead_blip[i]),obj_pos) < 25  then
