					ai.task_combat_ped(assigned[v].TRGT, assigned[v].PED,0,16)
				end
			end
		end
		funcs.doo.yield_while_true(f.on,2000)
	end
	ped.set_relationship_between_groups(2, group[1], group[2])
	ped.set_relationship_between_groups(2, group[2], group[1])
end)

