						g.tbl.clear_stuff.veh_count=g.tbl.clear_stuff.veh_count+1
					end
					_table[ent]=false
				end)
			end
		end
		::check::
		YIELD(0)
		for _, ENT in pairs(_table) do
			if ENT then
				goto check
			end
		end
		f.on=false
	end
end)g.tbl.clear_stuff.veh_do.hidden=true

