			local function should_tp()
				if  dist > 50 then
					return true
				elseif not have_los then
					if los_time == nil then
						los_time = API.util.t_s()+15
					elseif los_time < API.util.t_s() then
						los_time = API.util.t_s()+15
						return true
					end
				else
					los_time = API.util.t_s()+15
				end
